import * as React from "react";
import styles from "./Login.module.scss";
import technologo from "../../Images/Technologo.png";
import background from "../../Images/Technobackgroundimage.png";

export function Login() {
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");

  const handleEmailChange = (event) => {
    setEmail(event.target.value);
  };

  const handlePasswordChange = (event) => {
    setPassword(event.target.value);
  };

  const handleSignUpClick = () => {
    // code to handle sign up button click
  };

  const handleLoginSubmit = (event) => {
    event.preventDefault();
    // code to handle login form submission
  };

  return (
    <header>
      <div className={styles.maincontainer}>
        <div className={styles.container1}>
          <div id={styles.bgcolor}></div>
          <div className={styles.halfcircle}></div>
          <img src={technologo} alt="technologo" />

          <div>
            <h1>Login Form</h1>
            <form onSubmit={handleLoginSubmit}>
              <label htmlFor="email">Email Address:</label>
              <input
                type="email"
                id="email"
                name="email"
                value={email}
                onChange={handleEmailChange}
              />
              <br />
              <label htmlFor="password">Password:</label>
              <input
                type="password"
                id="password"
                name="password"
                value={password}
                onChange={handlePasswordChange}
              />
              <br />
              <button type="submit">Login</button>
            </form>
            <button onClick={handleSignUpClick}>Sign Up</button>
          </div>

          <div className={styles.bgcolor2}></div>
        </div>
        <div className={styles.container2}>
          <img src={background} alt="backgroundimage" />
        </div>
      </div>
    </header>
  );
}
